import React, { useState } from "react";
import {
  Grid,
  TextField,
  Button,
  Link,
  Box,
  Card,
  CardContent,
  Typography,
} from "@material-ui/core";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";

function AddMovie() {
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [cpassword, setCpassword] = useState("");
  const inputRef = React.useRef();

  const onClear = () => {
    setLname("");
    setPhone("");
    setEmail("");
    setPassword("");
    setCpassword("");
    setFname("");
    inputRef.current.focus();
  };

  return (
    <div className="App">
      <br />
      <br></br>
      <br></br>
      <br></br>
      <Grid>
        <Card
          style={{
            maxWidth: 50 + "%",
            padding: "20px 5px",
            margin: "0 auto",
            backgroundColor: "#f5f5f5",
            boxShadow: "0 10px 6px rgba(0, 0, 0, 0.16)",
          }}
        >
          <CardContent>
            <Typography
              gutterBottom
              variant="h5"
              style={{ fontFamily: "Source Sans Pro", fontSize: "34px" }}
            >
              Add a New Movie
            </Typography>
            <br />
            <form autoComplete="off">
              <Grid
                container
                spacing={2}
                style={{ display: "flex", flexDirection: "row" }}
              >
                <Grid item xs={6}>
                  <TextField
                    type="text"
                    placeholder="First Name"
                    inputRef={inputRef}
                    autoFocus
                    variant="outlined"
                    name="fname"
                    value={fname}
                    onChange={(e) => setFname(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="text"
                    placeholder="Last Name"
                    variant="outlined"
                    name="lname"
                    value={lname}
                    onChange={(e) => setLname(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="number"
                    placeholder="Phone Number"
                    variant="outlined"
                    name="phone"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="email"
                    placeholder="Email"
                    variant="outlined"
                    name="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    type="password"
                    placeholder="Password"
                    variant="outlined"
                    name="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    type="password"
                    placeholder="Confirm Password"
                    variant="outlined"
                    name="cpassword"
                    value={cpassword}
                    onChange={(e) => setCpassword(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid container spacing={3}>
                  <Grid item xs={6}>
                    <Button variant="contained" color="primary">
                      Create the Account
                    </Button>
                  </Grid>
                  <Grid item xs={6}>
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => onClear()}
                    >
                      Clear Fields
                    </Button>
                  </Grid>
                </Grid>
                <Grid>
                  <Box
                    sx={{ display: "flex", justifyContent: "center", mt: 2 }}
                  >
                    <Typography variant="subtitle1">
                      Already have an account?{" "}
                      <Link href="/login" underline="always">
                        Login
                      </Link>
                    </Typography>
                  </Box>
                </Grid>
              </Grid>
            </form>
          </CardContent>
        </Card>
      </Grid>
      <br />
    </div>
  );
}

export default AddMovie;
