import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Grid,
  TextField,
  Button,
  Link,
  Box,
  Card,
  CardContent,
  Typography,
} from "@material-ui/core";
import ButterToast, { Cinnamon } from "butter-toast";
import ErrorOutlineIcon from "@material-ui/icons/ErrorOutline";
import Swal from "sweetalert2";

function AddMovie() {
  const validation = () => {
    var Error = false;

    if (fname === "") {
      Swal.fire({
        icon: "error",
        title: "First Name Required",
      });
      Error = true;
    }

    if (lname === "") {
      Swal.fire({
        icon: "error",
        title: "Last Name Required",
      });
      Error = true;
    }

    if (phone === "") {
      Swal.fire({
        icon: "error",
        title: "Phone Number Required",
      });
      Error = true;
    }

    if (email === "") {
      Swal.fire({
        icon: "error",
        title: "Email Required",
      });
      Error = true;
    }

    if (password === "") {
      Swal.fire({
        icon: "error",
        title: "Password Required",
      });
      Error = true;
    }

    if (cpassword === "") {
      Swal.fire({
        icon: "error",
        title: "Confirm Password Required",
      });
      Error = true;
    } else if (password !== cpassword) {
      Swal.fire({
        icon: "error",
        title: "Password Does Not Match",
      });
      Error = true;
    }

    if (Error) {
      return false;
    }

    return true;
  };

  const SubmitForm = async (e) => {
    e.preventDefault();

    if (validation()) {
      const url = "http://localhost:4000/User";
      const data = JSON.stringify({
        fname: fname,
        lname: lname,
        phone: phone,
        email: email,
        password: password,
      });
      console.log(data);
      await axios
        .post(url, data, {
          headers: { "Content-Type": "application/json" },
        })
        .then(async (res) => {
          console.log(res);
          if (res.data.err !== "connection") {
            if (res.data.err === "email") {
              setEmail("");
              Swal.fire({
                icon: "error",
                title: "Email Already Exists",
                text: "Enter New Email or Back to Login",
              });
            } else if (res.data.err !== "already") {
              onClear();
              Swal.fire({
                icon: "success",
                title: "Registration Successful",
                text: "Back to Login Form and Enter Your Email and Password",
              });
            } else {
              ButterToast.raise({
                content: (
                  <Cinnamon.Crisp
                    title="Validation Error!"
                    content="Already Exists!"
                    scheme={Cinnamon.Crisp.SCHEME_RED}
                    icon={<ErrorOutlineIcon />}
                  />
                ),
              });
            }
          } else {
            ButterToast.raise({
              content: (
                <Cinnamon.Crisp
                  title="Validation Error!"
                  content="Connection Error!"
                  scheme={Cinnamon.Crisp.SCHEME_RED}
                  icon={<ErrorOutlineIcon />}
                />
              ),
            });
          }
        });
    }
  };

  return (
    <div className="App">
      <br />
      <br></br>
      <br></br>
      <br></br>
      {/* <Typography gutterBottom variant="h3" align="center">
        User Register Form
      </Typography> */}
      <Grid>
        <Card
          style={{
            maxWidth: 50 + "%",
            padding: "20px 5px",
            margin: "0 auto",
            backgroundColor: "#f5f5f5",
            boxShadow: "0 10px 6px rgba(0, 0, 0, 0.16)",
          }}
        >
          <CardContent>
            <Typography
              gutterBottom
              variant="h5"
              style={{ fontFamily: "Source Sans Pro", fontSize: "34px" }}
            >
              Create a New Account
            </Typography>
            <br />
            <form autoComplete="off" onSubmit={SubmitForm}>
              <Grid
                container
                spacing={2}
                style={{ display: "flex", flexDirection: "row" }}
              >
                <Grid item xs={6}>
                  <TextField
                    type="text"
                    placeholder="First Name"
                    inputRef={inputRef}
                    autoFocus
                    // label="First Name"
                    variant="outlined"
                    name="fname"
                    value={fname}
                    onChange={setFnameForm}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="text"
                    placeholder="Last Name"
                    // label="Last Name"
                    variant="outlined"
                    name="lname"
                    value={lname}
                    onChange={setLnameForm}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="number"
                    placeholder="Phone Number"
                    // label="Phone Number"
                    variant="outlined"
                    name="phone"
                    value={phone}
                    onChange={setPhoneForm}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="email"
                    placeholder="Email"
                    // label="Email"
                    variant="outlined"
                    name="email"
                    value={email}
                    onChange={setEmailForm}
                    fullWidth
                  />
                </Grid>

                <Grid item xs={12}>
                  <TextField
                    type="password"
                    placeholder="Password"
                    // label="Password"
                    variant="outlined"
                    name="password"
                    value={password}
                    onChange={setPasswordForm}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    type="password"
                    placeholder="Confirm Password"
                    // label="Confirm Password"
                    variant="outlined"
                    name="cpassword"
                    value={cpassword}
                    onChange={setCpasswordForm}
                    fullWidth
                  />
                </Grid>
                <Grid container spacing={3}>
                  <Grid item xs={6}>
                    <Button type="submit" variant="contained" color="primary">
                      Create the Account
                    </Button>
                  </Grid>
                  <Grid item xs={6}>
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => onClear()}
                    >
                      Clear Fields
                    </Button>
                  </Grid>
                </Grid>
                <Grid>
                  <Box
                    sx={{ display: "flex", justifyContent: "center", mt: 2 }}
                  >
                    <Typography variant="subtitle1">
                      Already have an account?{" "}
                      <Link href="/login" underline="always">
                        Login
                      </Link>
                    </Typography>
                  </Box>
                </Grid>
              </Grid>
            </form>
          </CardContent>
        </Card>
      </Grid>
      <br />
    </div>
  );
}

export default AddMovie;
