import React, { useState } from "react";
import {
  Grid,
  TextField,
  Button,
  Link,
  Box,
  Card,
  CardContent,
  Typography,
} from "@material-ui/core";

function AddMovie() {
  const [movieName, setMovieName] = useState("");
  const [ratings, setRatings] = useState("");
  const [votes, seetVotes] = useState("");
  const [minutes, setMinutes] = useState("");
  const [releasedate, setReleasedate] = useState("");
  const inputRef = React.useRef();

  const onClear = () => {
    setRatings("");
    seetVotes("");
    setMinutes("");
    setReleasedate("");
    setMovieName("");
    inputRef.current.focus();
  };

  return (
    <div className="App">
      <br />
      <br></br>
      <br></br>
      <br></br>
      <Grid>
        <Card
          style={{
            maxWidth: 50 + "%",
            padding: "20px 5px",
            margin: "0 auto",
            backgroundColor: "#f5f5f5",
            boxShadow: "0 10px 6px rgba(0, 0, 0, 0.16)",
          }}
        >
          <CardContent>
            <Typography
              gutterBottom
              variant="h5"
              style={{ fontFamily: "Source Sans Pro", fontSize: "34px" }}
            >
              Add a New Movie
            </Typography>
            <br />
            <form autoComplete="off">
              <Grid
                container
                spacing={2}
                style={{ display: "flex", flexDirection: "row" }}
              >
                <Grid item xs={6}>
                  <TextField
                    type="text"
                    placeholder="Movie Name"
                    inputRef={inputRef}
                    autoFocus
                    variant="outlined"
                    name="fname"
                    value={movieName}
                    onChange={(e) => setMovieName(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="text"
                    placeholder="Ratings"
                    variant="outlined"
                    name="lname"
                    value={ratings}
                    onChange={(e) => setRatings(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="number"
                    placeholder="Number of Votes"
                    variant="outlined"
                    name="phone"
                    value={votes}
                    onChange={(e) => seetVotes(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    type="email"
                    placeholder="Minutes"
                    variant="outlined"
                    name="email"
                    value={minutes}
                    onChange={(e) => setMinutes(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    type="password"
                    placeholder="Release Date"
                    variant="outlined"
                    name="password"
                    value={releasedate}
                    onChange={(e) => setReleasedate(e.target.value)}
                    fullWidth
                  />
                </Grid>
                <Grid container spacing={1}>
                  <Grid item xs={7}>
                    <Button variant="contained" color="primary">
                      Submit
                    </Button>
                  </Grid>
                  <Grid item xs={4}>
                    <Button
                      variant="contained"
                      color="secondary"
                      onClick={() => onClear()}
                    >
                      Clear Fields
                    </Button>
                  </Grid>
                </Grid>
                <Grid></Grid>
              </Grid>
            </form>
          </CardContent>
        </Card>
      </Grid>
      <br />
    </div>
  );
}

export default AddMovie;
